<template>
  <div class="startup-page">
    <div class="row-box bordered">
      <el-row :gutter="20">
        <el-col :xs="24" :sm="18" :lg="6">
          <img src="data:image/png;base64," class="img-fluid" :alt="user.payload.fio">
        </el-col>
        <el-col :xs="24" :sm="18" :lg="18">
          <div class="">
            <h1 class="startup-title">{{user.payload.fio}}</h1>
            <div class="company-description-icon">
              <a href="#" class="add-to-favorite btn" @click="addToFriends">
                <img src="/img/download.svg" alt="">
                <span>Добавить в друзья</span>
              </a>
            </div>
          </div>
          <el-row :gutter="20">
            <el-col :xs="24" :sm="24" :lg="24">
              <div class="startup-year-title">
                Email
              </div>
              <div class="startup-year">
                {{user.payload.email}}
              </div>
            </el-col>
            <el-col :xs="24" :sm="24" :lg="24">
              <div class="startup-year-title">
                Город
              </div>
              <div class="startup-year">
                {{user.payload.city}}
              </div>
            </el-col>
            <el-col :xs="24" :sm="24" :lg="24">
              <div class="startup-year-title">
                Опыт
              </div>
              <div class="startup-year">
                {{user.payload.experience}}
              </div>
            </el-col>
            <el-col :xs="24" :sm="24" :lg="24">
              <div class="startup-year-title">
                Должность
              </div>
              <div class="startup-year">
                {{user.payload.position}}
              </div>
            </el-col>
            <el-col :xs="24" :sm="24" :lg="24">
              <div class="startup-year-title">
                Навыки
              </div>
              <div class="startup-year">
                {{skills}}
              </div>
            </el-col>
          </el-row>
        </el-col>
      </el-row>
    </div>

  </div>
</template>

<script>
// import router from '@/router'
export default {
  name: 'User',
  props: ['id'],
  components: {
  },
  data() {
    return {
      skills: []
    }
  },
  methods: {
    addToFriends(){
      this.$store.dispatch('addToFriends', this.id)
    }
  },
  computed: {
    user() {
      return this.$store.getters.user
    }
  },
  watch: {
    user() {
      let arr = []
      this.user.payload.skills.forEach(item => {
        arr.push(item.name)
      })
      this.skills = arr.join(', ')
    }
  },
  created() {
    this.$store.dispatch('getUser', this.id)
  }
}
</script>

